import React from "react";
import { Button, Container, Media, Row } from "react-bootstrap";
import { useCart } from "../contexts/CartContext";

export default function Cart() {
  const { cartItems } = useCart();

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Shopping Cart</h1>
      <Media>
        <img
          width={64}
          height={64}
          className="align-self-start mr-3"
          src="holder.js/64x64"
          alt=""
        />
        <Media.Body>
          <h5>Product Name</h5>
          <Row>
            <p className="mr-2">Short Description</p>

            <p className="mr-2">Unit Price</p>

            <p className="mr-2">Quantity</p>

            <p>Total Line Price</p>
          </Row>
        </Media.Body>
      </Media>
      <div style={{ textAlign: "right" }}>
        <p>Sub-Total Cost</p>
        <p>Shipping Cost</p>
        <p>Tax</p>
        <p>Total Cost</p>
        <Button variant="success">Checkout</Button>
      </div>
    </Container>
  );
}
