import Chart from "chart.js";
import React, { useEffect } from "react";
import { Col, Container, Row, Table } from "react-bootstrap";

export default function DashboardSummary() {
  useEffect(() => {
    drawBarChart();
    drawPieChart();
  }, []);

  function drawBarChart() {
    const ctx = document.getElementById("myBarChart");
    new Chart(ctx, {
      type: "bar",
      data: {
        labels: ["Red", "Blue", "Yellow", "Green", "Purple", "Orange"],
        datasets: [
          {
            label: "# of Orders Shipped",
            data: [12, 19, 3, 5, 2, 3],
            backgroundColor: [
              "rgba(255, 99, 132, 0.2)",
              "rgba(54, 162, 235, 0.2)",
              "rgba(255, 206, 86, 0.2)",
              "rgba(75, 192, 192, 0.2)",
              "rgba(153, 102, 255, 0.2)",
              "rgba(255, 159, 64, 0.2)",
            ],
            borderColor: [
              "rgba(255, 99, 132, 1)",
              "rgba(54, 162, 235, 1)",
              "rgba(255, 206, 86, 1)",
              "rgba(75, 192, 192, 1)",
              "rgba(153, 102, 255, 1)",
              "rgba(255, 159, 64, 1)",
            ],
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
      },
    });
  }

  function drawPieChart() {
    const ctx = document.getElementById("myPieChart");
    new Chart(ctx, {
      type: "pie",
      data: {
        labels: ["Red", "Blue", "Yellow"],
        datasets: [
          {
            label: "My First Dataset",
            data: [300, 50, 100],
            backgroundColor: [
              "rgb(255, 99, 132)",
              "rgb(54, 162, 235)",
              "rgb(255, 205, 86)",
            ],
          },
        ],
      },
    });
  }

  function drawHorizontalBarChart() {
    const ctx = document.getElementById("myHorizontalBarChart");
    new Chart(ctx, {
      type: "horizontalBar",
      data: {
        labels: [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
        ],
        datasets: [
          {
            label: "Dataset 1",
            backgroundColor: "rgba(255, 159, 64, 0.2)",
            borderColor: "rgb(255, 159, 64, 0.2)",
            borderWidth: 1,
            data: [-31, 86, 32, 60, -49, -76, 78],
          },
          {
            label: "Dataset 2",
            backgroundColor: "rgba(0, 102, 255, 0.2)",
            borderColor: "rgb(0, 102, 255)",
            data: [-29, -67, -93, 29, -59, 29, 9],
          },
        ],
      },
    });
  }

  return (
    <>
      <Container>
        <Row>
          <Col>
            <canvas id="myBarChart"></canvas>
          </Col>
          <Col>
            <canvas id="myPieChart"></canvas>
          </Col>
        </Row>
        <Row>
          <Col>
            <canvas id="myHorizontalBarChart"></canvas>
          </Col>
        </Row>
      </Container>
      <Container>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
            <tr>
              <td>2</td>
              <td>Jacob</td>
              <td>Thornton</td>
              <td>@fat</td>
            </tr>
            <tr>
              <td>3</td>
              <td colSpan="2">Larry the Bird</td>
              <td>@twitter</td>
            </tr>
          </tbody>
        </Table>
      </Container>
    </>
  );
}
