import axios from "axios";
import React, { useEffect, useState } from "react";
import { Card, Container, Jumbotron, ListGroup } from "react-bootstrap";
import { Link } from "react-router-dom";
import { APIpaths } from "../API";
import styles from "./FeaturedItems.module.css";

export default function FeaturedItems() {
  const [featuredData, setFeaturedData] = useState();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(APIpaths.getAllItems)
      .then((res) => {
        setFeaturedData(res.data.results);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, []);

  if (!loading) {
    const topItems = featuredData.slice(0, 4);

    return (
      <Jumbotron className="mt-5 mb-5 text-center">
        <h3>Featured Items</h3>
        <Container className={styles.cardContainer}>
          {topItems.map((item) => {
            return (
              <Card key={item._id} className={styles.cardStyle}>
                <Card.Img
                  src={item.picture}
                  alt=""
                  loading="lazy"
                  className={styles.cardImage}
                />
                <Card.Body>
                  <Card.Title>{item.productName}</Card.Title>
                  <Card.Subtitle>{item.manufacturer}</Card.Subtitle>
                  <ListGroup variant="flush">
                    <ListGroup.Item>Rating: {item.rating}</ListGroup.Item>
                    <ListGroup.Item>
                      Price: ${item.price.toFixed(2)}
                    </ListGroup.Item>
                  </ListGroup>
                  <Link to={`/product/${item._id}`}>View</Link>
                </Card.Body>
              </Card>
            );
          })}
        </Container>
      </Jumbotron>
    );
  } else {
    return null;
  }
}
