import React, { useEffect, useState } from "react";
import axios from "axios";
import { APIpaths } from "../API";
import { Button, Col, Container, Image, Modal, Row } from "react-bootstrap";
import { useCart } from "../contexts/CartContext";
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";

export default function ProductDetail({ match }) {
  const [productData, setProductData] = useState();
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const { addToCart } = useCart();
  const { isAdmin, deleteProduct } = useAuth();

  const history = useHistory();

  function handleModalClose() {
    setShowModal(false);
  }

  function handleModalShow() {
    setShowModal(true);
  }

  function handleDelete(e) {
    e.preventDefault();

    try {
      deleteProduct(productData._id);
      history.push("/products");
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    setLoading(true);
    axios
      .get(APIpaths.getOneItem + match.params.id)
      .then((res) => {
        setProductData(res.data);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, [match.params.id]);

  return (
    <Container className="min-vh-100 mt-5">
      {!loading && isAdmin ? (
        <Modal
          show={showModal}
          onHide={handleModalClose}
          backdrop="static"
          keyboard={false}
          animation="false"
        >
          <Modal.Header closeButton>
            <Modal.Title>Confirm Delete</Modal.Title>
          </Modal.Header>
          <Modal.Body>Are you sure you want to delete this product?</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleModalClose}>
              Cancel
            </Button>
            <Button variant="danger" onClick={handleDelete}>
              Confirm Delete
            </Button>
          </Modal.Footer>
        </Modal>
      ) : null}
      <Row xs={1} md={2}>
        {loading ? (
          <p>Loading...</p>
        ) : productData ? (
          <>
            <Col>
              <Image src={productData.picture} fluid />
            </Col>
            <Col>
              <h2>{productData.productName}</h2>
              <h4>Manufacturer: {productData.manufacturer}</h4>
              <p>Rating: {productData.rating}</p>
              <p>Stock: {productData.stockQty}</p>
              <p>UPC: {productData.upc}</p>
              <b>Price: ${productData.price.toFixed(2)}</b>
              <br />
              <Button
                variant="primary"
                onClick={() => addToCart(productData._id)}
              >
                Add to Card
              </Button>
            </Col>
          </>
        ) : (
          <Col>No content to display.</Col>
        )}
      </Row>
      {isAdmin ? (
        <Row>
          <hr />
          <h3>Admin Actions</h3>

          <Button
            variant="warning"
            className="mb-2"
            as={Link}
            to={`/edit-product/${productData._id}`}
          >
            Edit Product
          </Button>
          <br />
          <Button variant="danger" onClick={handleModalShow}>
            Delete Product
          </Button>
        </Row>
      ) : null}
    </Container>
  );
}
