export const serverAPI = "http://localhost:5000"; // Local development

export const APIpaths = {
  // Prodcts
  // GET all
  getAllItems: `${serverAPI}/api/products`,
  // GET one (+ :id)
  getOneItem: `${serverAPI}/api/products/`,
  // GET search (+ ?q=)
  searchQuery: `${serverAPI}/api/products`,
  // POST
  createItem: `${serverAPI}/api/products/`,
  // UPDATE (+ :id)
  updateItem: `${serverAPI}/api/products/`,
  // DELETE (+ :id)
  deleteItem: `${serverAPI}/api/products/delete/`,

  // Authentication
  // POST
  loginUser: `${serverAPI}/api/auth`,
  // GET
  getUser: `${serverAPI}/api/auth/user`,

  // Register
  // POST
  registerUser: `${serverAPI}/api/users`,

  // Update user info
  // PUT
  updateGeneral: `${serverAPI}/api/users/update`,
  updatePass: `${serverAPI}/api/users/update/password`,
};
