import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { APIpaths } from "../API";
import useLocalStorage from "../hooks/useLocalStorage";

const AuthContext = React.createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState();
  const [loading, setLoading] = useState(true);

  const [authToken, setAuthToken] = useLocalStorage("auth-token", null);

  async function register(name, email, password, address) {
    const res = await axios.post(APIpaths.registerUser, {
      name,
      email,
      password,
      address,
    });

    setCurrentUser(res.data.user);
    setAuthToken(res.data.token);
  }

  async function login(email, password) {
    const res = await axios.post(APIpaths.loginUser, {
      email,
      password,
    });

    setCurrentUser(res.data.user);
    setAuthToken(res.data.token);
  }

  function logout() {
    setCurrentUser(undefined);
    setAuthToken(null);
  }

  async function getUserFromToken(tokenValue) {
    try {
      const res = await axios.get(APIpaths.getUser, {
        headers: { "x-auth-token": tokenValue },
      });
      setCurrentUser(res.data);
    } catch (error) {
      return;
    }
  }

  async function updateEmail(email) {
    const data = { email };
    const config = { headers: { "x-auth-token": authToken } };

    const res = await axios.put(APIpaths.updateEmail, data, config);

    setCurrentUser(res.data);
  }

  async function updatePassword(password) {
    const data = { password };
    const config = { headers: { "x-auth-token": authToken } };

    await axios.put(APIpaths.updatePass, data, config);
  }

  async function updateAddress(address) {
    const data = { address };
    const config = { headers: { "x-auth-token": authToken } };

    const res = await axios.put(APIpaths.updateAddress, data, config);

    setCurrentUser(res.data);
  }

  useEffect(() => {
    if (!currentUser && authToken !== null) getUserFromToken(authToken);
    setCurrentUser(currentUser);
    setLoading(false);
  }, [authToken, currentUser]);

  const value = {
    currentUser,
    login,
    register,
    logout,
    updateEmail,
    updatePassword,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
