import React from "react";
import { ListGroup } from "react-bootstrap";

export default function ListGroupCart(props) {
  return (
    <ListGroup style={{ maxHeight: "90vh", overflowY: "auto" }}>
      {props.cartItems.map((item) => {
        return (
          <ListGroup.Item
            key={item._id}
            className="d-flex justify-content-between align-items-center"
          >
            {item.productName} ({item.qty})
            <span>${(item.price * item.qty).toFixed(2)}</span>
          </ListGroup.Item>
        );
      })}
      <ListGroup.Item>
        <em className="d-flex justify-content-between align-items-center">
          Sub Total:<span>${props.subTotal.toFixed(2)}</span>
        </em>
        <em className="d-flex justify-content-between align-items-center">
          Tax:<span>${props.taxCost.toFixed(2)}</span>
        </em>
        <em className="d-flex justify-content-between align-items-center">
          Shipping:<span>${props.shippingCost.toFixed(2)}</span>
        </em>
      </ListGroup.Item>

      <ListGroup.Item>
        <strong className="d-flex justify-content-between align-items-center">
          Total
          <span>${props.totalCost.toFixed(2)}</span>
        </strong>
      </ListGroup.Item>
    </ListGroup>
  );
}
