import React, { useEffect, useState } from "react";
import {
  Card,
  Col,
  Container,
  ListGroup,
  Pagination,
  Row,
} from "react-bootstrap";
import { APIpaths } from "../API";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Inventory() {
  const [products, setProducts] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    axios
      .get(APIpaths.getAllItems)
      .then((res) => {
        setProducts(res.data);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Inventory page</h1>

      <Container>
        <Row>
          {loading ? (
            <p>Loading...</p>
          ) : products !== null ? (
            products.results.map((item, index) => {
              return (
                <Card key={index}>
                  <Card.Img src={item.picture} alt="" loading="lazy" />
                  <Card.Body>
                    <Card.Title>{item.productName}</Card.Title>
                    <Card.Subtitle>{item.manufacturer}</Card.Subtitle>
                    <ListGroup variant="flush">
                      <ListGroup.Item>Rating: {item.rating}</ListGroup.Item>
                      <ListGroup.Item>Price: ${item.price}</ListGroup.Item>
                    </ListGroup>
                    <Link to={`/product/${item._id}`}>View</Link>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <Col>
              <p>No content to display.</p>
            </Col>
          )}
        </Row>
      </Container>

      <Pagination>
        <Pagination.First />
        <Pagination.Prev />
        <Pagination.Item>{1}</Pagination.Item>
        <Pagination.Ellipsis />

        <Pagination.Item>{10}</Pagination.Item>
        <Pagination.Item>{11}</Pagination.Item>
        <Pagination.Item active>{12}</Pagination.Item>
        <Pagination.Item>{13}</Pagination.Item>
        <Pagination.Item disabled>{14}</Pagination.Item>

        <Pagination.Ellipsis />
        <Pagination.Item>{20}</Pagination.Item>
        <Pagination.Next />
        <Pagination.Last />
      </Pagination>
    </Container>
  );
}
