import React from "react";
import { Pagination } from "react-bootstrap";

export default function PaginationNav({
  firstPage,
  lastPage,
  prevPage,
  nextPage,
  handleGetNextPage,
  handleGetPreviousPage,
  handleGoToPage,
}) {
  const pages = [...Array(lastPage).keys()].map((idx) => idx + 1);

  return (
    <Pagination>
      <Pagination.First onClick={() => handleGoToPage(firstPage)} />
      <Pagination.Prev
        onClick={() => handleGetPreviousPage()}
        disabled={prevPage === null ? true : false}
      />

      {pages.map((page) => {
        return (
          <Pagination.Item key={page} onClick={() => handleGoToPage(page)}>
            {page}
          </Pagination.Item>
        );
      })}
      <Pagination.Next
        onClick={() => handleGetNextPage()}
        disabled={nextPage === null ? true : false}
      />
      <Pagination.Last onClick={() => handleGoToPage(lastPage)} />
    </Pagination>
  );
}
