import React, { useEffect, useState } from "react";
import axios from "axios";
import { APIpaths } from "../API";
import { Button, Col, Container, Row } from "react-bootstrap";
import { useCart } from "../contexts/CartContext";
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
import ConfirmDeleteModal from "../components/ConfirmDeleteModal";

export default function ProductDetail({ match }) {
  const [productData, setProductData] = useState();
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const { addToCart } = useCart();
  const { isAdmin, deleteProduct } = useAuth();

  const history = useHistory();

  function handleModalClose() {
    setShow(false);
  }

  function handleModalShow() {
    setShow(true);
  }

  function handleDelete(e) {
    e.preventDefault();

    try {
      deleteProduct(productData._id);
      history.push(`/products`);
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    setLoading(true);
    axios
      .get(APIpaths.getOneItem + match.params.id)
      .then((res) => {
        setProductData(res.data);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, [match.params.id]);

  return (
    <Container className="min-vh-100 mt-5">
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <Row>
        {loading ? (
          <p>Loading...</p>
        ) : productData ? (
          <Container>
            <h2>{productData.productName}</h2>
            <img src={productData.picture} alt="" />
            <h4>Manufacturer: {productData.manufacturer}</h4>
            <p>Rating: {productData.rating}</p>
            <p>Stock: {productData.stockQty}</p>
            <p>UPC: {productData.upc}</p>
            <b>Price: ${productData.price.toFixed(2)}</b>
            <br />
            <Button
              variant="primary"
              onClick={() => addToCart(productData._id)}
            >
              Add to Card
            </Button>
            {isAdmin ? (
              <>
                <hr />
                <h3>Admin Actions</h3>

                <Button
                  variant="warning"
                  className="mb-2"
                  as={Link}
                  to={`/edit-product/${productData._id}`}
                >
                  Edit Product
                </Button>
                <br />
                <Button variant="danger" onClick={() => handleShow(true)}>
                  Delete Product
                </Button>
              </>
            ) : null}
          </Container>
        ) : (
          <Col>No content to display.</Col>
        )}
      </Row>
    </Container>
  );
}
