import React, { useState } from "react";
import { useRef } from "react";
import { Alert, Button, Card, Col, Form } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import { useAuth } from "../contexts/AuthContext";

export default function UpdateAccount() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmedRef = useRef();

  const [error, setError] = useState("");

  const { currentUser, updatePassword, updateEmail } = useAuth();
  const history = useHistory();

  function handleSubmit(e) {
    e.preventDefault();
    if (passwordRef.current.value !== passwordConfirmedRef.current.value) {
      return setError("Passwords do not match.");
    }

    const promises = [];

    setError("");

    if (emailRef.current.value !== currentUser.email) {
      promises.push(updateEmail(emailRef.current.value));
    }

    if (passwordRef.current.value) {
      promises.push(updatePassword(passwordRef.current.value));
    }

    Promise.all(promises)
      .then(() => history.push("/"))
      .catch(() => {
        setError("Failed to update account.");
      });
  }

  return (
    <>
      <Card>
        <Card.Body>
          <h2 className="text-center mb-4">Update Profile</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group id="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                ref={emailRef}
                required
                defaultValue={currentUser.email}
              />
            </Form.Group>

            <Form.Group id="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                ref={passwordRef}
                placeholder="Leave blank to keep the same"
              />
            </Form.Group>

            <Form.Group id="password-confirm">
              <Form.Label>Password Confirmation</Form.Label>
              <Form.Control
                type="password"
                ref={passwordConfirmedRef}
                placeholder="Leave blank to keep the same"
              />
            </Form.Group>

            <Form.Group id="address-line">
              <Form.Label>Address</Form.Label>
              <Form.Control placeholder="1234 Main St" />
            </Form.Group>

            <Form.Row>
              <Form.Group as={Col} controlId="city">
                <Form.Label>City</Form.Label>
                <Form.Control />
              </Form.Group>

              <Form.Group as={Col} controlId="province">
                <Form.Label>Province</Form.Label>
                <Form.Control as="select" defaultValue="Choose...">
                  <option selected disabled>
                    Choose...
                  </option>
                  <option>Alberta</option>
                  <option>British Coloumbia</option>
                  <option>Manitoba</option>
                  <option>New Brunswick</option>
                  <option>Newfoundland and Labrador</option>
                  <option>Nova Scotia</option>
                  <option>Ontario</option>
                  <option>Prince Edward Island</option>
                  <option>Quebec</option>
                  <option>Saskatechewan</option>
                  <option>Northwest Territories</option>
                  <option>Nunavut</option>
                  <option>Yukon</option>
                </Form.Control>
              </Form.Group>

              <Form.Group as={Col} controlId="postal-code">
                <Form.Label>Postal Code</Form.Label>
                <Form.Control />
              </Form.Group>
            </Form.Row>
            <Button className="mr-1" type="submit">
              Update
            </Button>
            <Button variant="secondary" as={Link} to="/">
              Cancel
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </>
  );
}
