import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container, ListGroup, Row } from "react-bootstrap";
import { APIpaths } from "../API";
import axios from "axios";
import { Link } from "react-router-dom";
// import PaginationNav from "../components/PaginationNav";
import styles from "./Products.module.css";
import LoadingSpinner from "../components/LoadingSpinner";

export default function Search({ query }) {
  const totalPages = useRef(null);

  const [currentPage, setCurrentPage] = useState(1);
  const [nextPage, setNextPage] = useState(null);
  const [prevPage, setPrevPage] = useState(null);

  const [products, setProducts] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    handleGetData(query);
  }, [query]);

  async function handleGetData(query) {
    try {
      const results = await axios.get(`${APIpaths.searchQuery}?q=${query}`);

      setProducts(results.data);

      if (results.data.next) {
        setNextPage(results.data.next.page);
      } else {
        setNextPage(null);
      }

      if (results.data.previous) {
        setPrevPage(results.data.previous.page);
      } else {
        setPrevPage(null);
      }

      if (results.data.total.pages !== totalPages.current) {
        totalPages.current = results.data.total.pages;
      }

      setLoading(false);
    } catch (error) {
      return console.log(error);
    }
  }

  function handleGetNextPage() {
    if (nextPage !== null) {
      setCurrentPage(nextPage);
    } else {
      return;
    }
  }

  function handleGetPreviousPage() {
    if (prevPage !== null) {
      setCurrentPage(prevPage);
    } else {
      return;
    }
  }

  function handleGoToPage(pageNum) {
    if (currentPage <= totalPages.current && pageNum > 0) {
      setCurrentPage(pageNum);
    } else {
      return;
    }
  }

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Search Results</h1>
      <p>showing results for "{query}"</p>

      <Container>
        <Row className={styles.rowFlex}>
          {loading ? (
            <LoadingSpinner />
          ) : products !== null ? (
            products.results.map((item, index) => {
              return (
                <Card key={index} className={styles.cardStyle}>
                  <Card.Img
                    src={item.picture}
                    alt=""
                    loading="lazy"
                    className={styles.cardImage}
                  />
                  <Card.Body>
                    <Card.Title>{item.productName}</Card.Title>
                    <Card.Subtitle>{item.manufacturer}</Card.Subtitle>
                    <ListGroup variant="flush">
                      <ListGroup.Item>Rating: {item.rating}</ListGroup.Item>
                      <ListGroup.Item>
                        Price: ${item.price.toFixed(2)}
                      </ListGroup.Item>
                    </ListGroup>
                    <Link to={`/product/${item._id}`}>View</Link>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <Col>
              <p>No content to display.</p>
            </Col>
          )}
        </Row>
      </Container>

      {/* {totalPages.current > 1 ? (
        <PaginationNav
          totalPages={totalPages.current}
          prevPage={prevPage}
          nextPage={nextPage}
          currentPage={currentPage}
          handleGetNextPage={handleGetNextPage}
          handleGetPreviousPage={handleGetPreviousPage}
          handleGoToPage={handleGoToPage}
        />
      ) : null} */}
    </Container>
  );
}
