import React, { useEffect, useRef, useState } from "react";
import { Card, Col, Container, ListGroup, Row } from "react-bootstrap";
import { APIpaths } from "../API";
import axios from "axios";
import { Link } from "react-router-dom";
import PaginationNav from "../components/PaginationNav";

export default function Inventory() {
  const [currentPage, setCurrentPage] = useState(1);
  const [firstPage, setFirstPage] = useState(1);
  const [lastPage, setLastPage] = useState(null);
  const [nextPage, setNextPage] = useState(null);
  const [prevPage, setPrevPage] = useState(null);

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  const pagesArr = useRef([null]);

  useEffect(() => {
    setLoading(true);
    axios
      .get(`${APIpaths.getAllItems}`)
      .then((res) => {
        setProducts(res.data);

        if (res.data.next) {
          setNextPage(res.data.next.page);
        } else {
          setNextPage(null);
        }

        if (res.data.previous) {
          setPrevPage(res.data.previous.page);
        } else {
          setPrevPage(null);
        }

        if (res.data.total) {
          setLastPage(res.data.total.pages);
        }

        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    setLoading(true);
    axios
      .get(`${APIpaths.getAllItems}?page=${currentPage.toString()}&limit=10`)
      .then((res) => {
        setProducts(res.data);

        if (res.data.next) {
          setNextPage(res.data.next.page);
        } else {
          setNextPage(null);
        }

        if (res.data.previous) {
          setPrevPage(res.data.previous.page);
        } else {
          setPrevPage(null);
        }

        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, [currentPage]);

  function handleGetNextPage() {
    if (nextPage !== null) {
      setCurrentPage(nextPage);
    } else {
      return;
    }
  }

  function handleGetPreviousPage() {
    if (prevPage !== null) {
      setCurrentPage(prevPage);
    } else {
      return;
    }
  }

  function handleGoToPage(pageNum) {
    if (currentPage <= lastPage && pageNum > 0) {
      setCurrentPage(pageNum);
    } else {
      return;
    }
  }

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Inventory page</h1>

      <Container>
        <Row>
          {loading ? (
            <p>Loading...</p>
          ) : products.length > 0 ? (
            products.results.map((item, index) => {
              return (
                <Card key={index}>
                  <Card.Img src={item.picture} alt="" loading="lazy" />
                  <Card.Body>
                    <Card.Title>{item.productName}</Card.Title>
                    <Card.Subtitle>{item.manufacturer}</Card.Subtitle>
                    <ListGroup variant="flush">
                      <ListGroup.Item>Rating: {item.rating}</ListGroup.Item>
                      <ListGroup.Item>
                        Price: ${item.price.toFixed(2)}
                      </ListGroup.Item>
                    </ListGroup>
                    <Link to={`/product/${item._id}`}>View</Link>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <Col>
              <p>No content to display.</p>
            </Col>
          )}
        </Row>
      </Container>

      <PaginationNav
        firstPage
        lastPage
        prevPage
        nextPage
        handleGetNextPage
        handleGetPreviousPage
        handleGoToPage
      />
    </Container>
  );
}
