import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { APIpaths } from "../API";
import useLocalStorage from "../hooks/useLocalStorage";

const AuthContext = React.createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState();
  const [loading, setLoading] = useState(true);

  const [authToken, setAuthToken] = useLocalStorage("auth-token", "");

  async function register(name, email, password) {
    const res = await axios.post(APIpaths.registerUser, {
      name,
      email,
      password,
    });

    setCurrentUser(res.data.user);
    setAuthToken(res.data.token);
  }

  async function login(email, password) {
    const res = await axios.post(APIpaths.loginUser, {
      email,
      password,
    });

    setCurrentUser(res.data.user);
    setAuthToken(res.data.token);
  }

  function logout() {
    setCurrentUser(undefined);
    setAuthToken("");
  }

  async function getUserFromToken(tokenValue) {
    const res = await axios.post(APIpaths.getUser, {
      headers: { "x-auth-token": tokenValue },
    });

    return res.data.user;
  }

  useEffect(() => {
    console.log(`From AuthContext, authToken: ${authToken}`);
    setCurrentUser(currentUser);
    setLoading(false);
  }, [currentUser]);

  const value = {
    currentUser,
    login,
    register,
    logout,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
