import React from "react";
import { ListGroup } from "react-bootstrap";

export default function ListGroupCart({
  cartItems,
  subTotalCounts,
  totalCost,
}) {
  return (
    <ListGroup style={{ maxHeight: "90vh", overflowY: "auto" }}>
      {cartItems.map((item) => {
        return (
          <ListGroup.Item
            key={item._id}
            className="d-flex justify-content-between align-items-center"
          >
            {item.productName} ({item.qty})
            <span>${(item.price * item.qty).toFixed(2)}</span>
          </ListGroup.Item>
        );
      })}
      <ListGroup.Item>
        {subTotalCounts.map((count, idx) => {
          return (
            <em
              key={idx}
              className="d-flex justify-content-between align-items-center"
            >
              {count}:<span>${count.toFixed(2)}</span>
            </em>
          );
        })}
      </ListGroup.Item>

      <ListGroup.Item>
        <strong className="d-flex justify-content-between align-items-center">
          Total
          <span>${totalCost.toFixed(2)}</span>
        </strong>
      </ListGroup.Item>
    </ListGroup>
  );
}
