import React from "react";
import { Container, Button, Table, Image } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useCart } from "../contexts/CartContext";

export default function Checkout() {
  const { cartItems } = useCart();

  return (
    <Container>
      <h1>Checkout</h1>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>#</th>
            <th>Product Image</th>
            <th>Product Name</th>
            <th>Quantity</th>
            <th>Unit Price</th>
            <th>Product Sub-Total</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item, idx) => {
            return (
              <tr key={item._id}>
                <td>{idx + 1}</td>
                <td>
                  <Image src={item.picture} thumbnail />
                </td>
                <td>
                  <Link to={`/product/${item._id}`}>{item.productName}</Link>
                </td>
                <td>${item.price}</td>
                <td>${(item.price * item.qty).toFixed(2)}</td>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <p>Confirm shipping address</p>
      <p>Select payment method</p>
      <Button variant="primary">Confirm Order</Button>
    </Container>
  );
}
