import React, { useEffect, useState } from "react";
import { Card, Col, Container, ListGroup, Row } from "react-bootstrap";
import { APIpaths } from "../API";
import axios from "axios";
import { Link, useLocation } from "react-router-dom";
import styles from "./Products.module.css";
import LoadingSpinner from "../components/LoadingSpinner";

export default function Search({ queryParam }) {
  const [products, setProducts] = useState(null);
  const [loading, setLoading] = useState(true);

  const query = new URLSearchParams(useLocation().search.get("q"));

  useLocation();

  useEffect(() => {
    setLoading(true);
    handleGetData(query);
  }, [query]);

  async function handleGetData(query) {
    try {
      const results = await axios.get(`${APIpaths.searchQuery}?q=${query}`);

      setProducts(results.data);

      setLoading(false);
    } catch (error) {
      return console.log(error);
    }
  }

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Search Results</h1>

      <Container>
        {!loading && products.length > 0 ? (
          <p>Showing results for "{query}"</p>
        ) : null}
        <Row className={styles.rowFlex}>
          {loading ? (
            <LoadingSpinner />
          ) : products.length > 0 ? (
            products.map((item, index) => {
              return (
                <Card key={index} className={styles.cardStyle}>
                  <Card.Img
                    src={item.picture}
                    alt=""
                    loading="lazy"
                    className={styles.cardImage}
                  />
                  <Card.Body>
                    <Card.Title>{item.productName}</Card.Title>
                    <Card.Subtitle>{item.manufacturer}</Card.Subtitle>
                    <ListGroup variant="flush">
                      <ListGroup.Item>Rating: {item.rating}</ListGroup.Item>
                      <ListGroup.Item>
                        Price: ${item.price.toFixed(2)}
                      </ListGroup.Item>
                    </ListGroup>
                    <Link to={`/product/${item._id}`}>View</Link>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <Col>
              <p>No content to display.</p>
            </Col>
          )}
        </Row>
      </Container>

      {/* {totalPages.current > 1 ? (
        <PaginationNav
          totalPages={totalPages.current}
          prevPage={prevPage}
          nextPage={nextPage}
          currentPage={currentPage}
          handleGetNextPage={handleGetNextPage}
          handleGetPreviousPage={handleGetPreviousPage}
          handleGoToPage={handleGoToPage}
        />
      ) : null} */}
    </Container>
  );
}
