import React from "react";
import { Button, Container, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useCart } from "../contexts/CartContext";

export default function Cart() {
  const { cartItems, addToCart, removeFromCart } = useCart();

  // const subTotal = cartItems.reduce(
  //   (accumulator, current) => accumulator + current.qty * current.price,
  //   0
  // );
  // const taxCost = subTotal * 0.13;
  // const shippingCost = subTotal > 50 ? 0 : 20;
  // const totalCost = subTotal + taxCost + shippingCost;

  return (
    <Container className="min-vh-100 mt-5">
      <h1>Shopping Cart</h1>
      {cartItems.length > 0 ? (
        <>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>#</th>
                <th>Product Name</th>
                <th>Quantity</th>
                <th>Unit Price</th>
                <th>Product Sub-Total</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, idx) => {
                return (
                  <tr key={idx}>
                    <td>{idx + 1}</td>
                    <td>
                      <Link to={`/product/${item._id}`}>
                        {item.productName}
                      </Link>
                    </td>
                    <td>
                      <Button
                        size="sm"
                        onClick={() => removeFromCart(item._id)}
                      >
                        -
                      </Button>
                      <span className="px-2">{item.qty}</span>
                      <Button size="sm" onClick={() => addToCart(item._id)}>
                        +
                      </Button>
                    </td>
                    <td>{item.price}</td>
                    <td>{(item.price * item.qty).toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
          {/* <div style={{ textAlign: "right" }}>
            <p>Sub-Total Cost: {subTotal.toFixed(2)}</p>
            <p>Shipping Cost: {shippingCost.toFixed(2)}</p>
            <p>Tax: {taxCost.toFixed(2)}</p>
            <p>Total Cost: {totalCost.toFixed(2)}</p>
            <Button variant="success">Checkout</Button>
          </div> */}
        </>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </Container>
  );
}
