import axios from "axios";
import React from "react";
import { useState } from "react";
import { useRef } from "react";
import { Alert, Button, Container, Form } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import { APIpaths } from "../API";

export default function AddProduct() {
  const productName = useRef();
  const manufacturer = useRef();
  const price = useRef();
  const stockQty = useRef();
  const upc = useRef();
  const picture = useRef();

  const [error, setError] = useState("");

  const history = useHistory();

  async function handleSubmit(e) {
    e.preventDefault();

    try {
      setError("");

      const newItemData = {
        productName: productName.current,
        manufacturer: manufacturer.current,
        price: manufacturer.current,
        stockQty: stockQty.current,
        upc: upc.current,
        picture: picture.current,
      };

      axios.post(APIpaths.createItem, newItemData);

      history.push("/");
    } catch (error) {
      setError("Failed to create product.");
    }
  }
  return (
    <Container className="min-vh-100 mt-5">
      <h1>Add A Product</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="productName">
          <Form.Label>Product Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter a product name"
            ref={productName}
            required
          />
        </Form.Group>

        <Form.Group controlId="manufacturer">
          <Form.Label>Manufacturer</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter manufacturer"
            ref={manufacturer}
            required
          />
        </Form.Group>

        <Form.Group controlId="price">
          <Form.Label>Price</Form.Label>
          <Form.Control
            type="number"
            step="0.01"
            placeholder="100.00"
            ref={price}
            required
          />
        </Form.Group>

        <Form.Group controlId="upcId">
          <Form.Label>UPC</Form.Label>
          <Form.Control
            type="text"
            placeholder="123456789-0"
            ref={upc}
            required
          />
        </Form.Group>

        <Form.Group controlId="picutreId">
          <Form.Label>Picture</Form.Label>
          <Form.Control
            type="text"
            placeholder="http://..."
            ref={picture}
            required
          />
        </Form.Group>

        <hr />

        <Form.Group controlId="stockQty">
          <Form.Label>Stock Quantity</Form.Label>
          <Form.Control
            type="number"
            step="1"
            placeholder="50"
            ref={stockQty}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </Container>
  );
}
